import React, {Component} from 'react';

class Product extends Component {

    constructor(props) {
        super(props);
    }

    render() {

        const [name, price, index] = props.products;

        return (
            <li key={index}>
                {name} | ${price} | <button onClick={() => this.deleteProduct(index)}>DEL</button>
            </li>
        );
    }
}

export default Product;
