import React, {Component} from 'react';

class CreateProduct extends Component {

    constructor(props) {
        super(props);

        this.createProduct = this.createProduct.bind(this);
    }

    createProduct() {
        const { createProduct } = this.props;
        const product = { name: "MacBook Pro", price: 2000 };
        createProduct(product);
    }

    render() {

        return (
            <form>
                <input type="text" value="" placeholder="Product Name"/>
                <input type="number" value="" placeholder="Product Price"/>

                <button type="submit" onSubmit={this.createProduct}>Create Product</button>
            </form>
        );
    }
}

export default CreateProduct;
