import React, {Component} from 'react';
import products from './crud/products'; // Mimic DB

localStorage.setItem('products', JSON.stringify(products));

class Crud extends Component {

    constructor(props) {
        super(props);

        this.state = {
            title: 'Products Manager',
            products: []
        };
    }

    componentWillMount() {
        this.getProducts()
    }

    getProducts() {
        const products = JSON.parse(localStorage.getItem('products'));
        this.setState({products});
    }

    showProducts() {
        return(
            <ul>
                {products.map((product, index) =>
                    <li key={index}>
                        {product.name} | ${product.price} | <button onClick={this.deleteProduct}>DEL</button>
                    </li>
                )}
            </ul>
        )
    }

    deleteProduct(index) {
        const products = this.state.products.filter((product, i) => i !== index )
        console.log(products);
        return products;
    }

    render() {
        return (
            <div>
                <h1>{this.state.title}</h1>
                <hr/>
                {this.state.products && this.showProducts()}
            </div>
        );
    }
}

export default Crud;
