import React, {Component} from 'react';
import SandCoxChild from './SandBoxChild';

class SandBox extends Component {

    constructor(props) {
        super(props);

        this.state = {
          name: 'React JS'  
        };

        this.onClick = this.onClick.bind(this);
        this.onDoubleClick = this.onDoubleClick.bind(this);
    }

	onClick() {
        const name = this.input.value;

        this.setState({name});
		console.log(`Application Name: ${this.input.value}`);
	}

    onDoubleClick() {
        this.setState({
            name: "Sand Box Child",
            id: 0
        });
    }

    onChange(event) {
	    document.querySelector("#name").textContent = event.target.value;
    }


    componentWillMount() { console.log('componentWillMount'); }
    componentDidMount() { console.log('componentDidMount'); }

    componentWillUpdate(nextProps, nextState) {
        console.log('componentWillUpdate >>>');
        console.log(this.props, nextProps);
        console.log(this.state, nextState);
    }
    // componentDidUpdate() {  }

	render() {

	    const project = {
	        title: "San Box",
            desc: "A place to practice."
        };

		return (
			<div>
                <h1>{this.state.name}</h1>
                <hr/>
                { project && project.title && <h3>{project.title}</h3> }
                { project && project.desc && <p>{project.desc}</p> }

                <input type="text" onChange={this.onChange} ref={input => this.input = input} />
                <button type="submit" onClick={this.onClick}>Submit</button>
                <p id="name"></p>

                <hr/>

                <SandCoxChild name="Gigo" id={this.state.id} onDoubleClick={this.onDoubleClick} />
			</div>
		)
	}

}

export default SandBox;
