import React, {Component} from 'react';

class SandBox extends Component {

	onClick() {
		console.log('Boom!');
	}

    onChange(event) {
	    document.querySelector("#price").textContent = event.target.value;
    }

	render() {

	    const project = {
	        title: "San Box",
            desc: "A place to practice.",
            price: null
        };

		return (
			<div>
                { project && project.title && <h1 onClick={this.onClick}>{project.title}</h1> }
                { project && project.desc && <p>{project.desc}</p> }

                <input type="text" onChange={this.onChange} />
                <p id="price">{ project && project.price && `$${project.price}` }</p>
			</div>
		)
	}

}

export default SandBox;
