import React, {Component} from 'react';

class CreateProduct extends Component {

    constructor(props) {
        super(props);

        this.createProduct = this.createProduct.bind(this);
    }

    createProduct(event) {
        event.preventDefault();

        const { createProduct } = this.props;
        const inputName = document.querySelector("[name='prodName']");
        const inputPrice = document.querySelector("[name='prodPrice']");

        const product = { name: inputName.value, price: inputPrice.value };

        inputName.value = '';
        inputPrice.value = '';

        createProduct(product);

    }

    render() {

        return (
            <form>
                <input type="text" name="prodName" placeholder="Product Name"/>
                <input type="number" name="prodPrice" placeholder="Product Price"/>

                <button type="submit" onClick={this.createProduct}>Create Product</button>
            </form>
        );
    }
}

export default CreateProduct;
