import React, {Component} from 'react';

class Crud extends Component {

    constructor(props) {
        super(props);

        this.state = {
            title: 'CRUD',
            products: [
                { name: "iPhone", price: 1000 },
                { name: "iPad", price: 700 },
                { name: "iMac", price: 1500 },
            ]
        };
    }

    render() {
        return (
            <div>
                <h1>{this.state.title}</h1>
                <hr/>
            </div>
        );
    }
}

export default Crud;
