import React, {Component} from 'react';
import products from './crud/products'; // Mimic DB

localStorage.setItem('products', JSON.stringify(products));

class Crud extends Component {

    constructor(props) {
        super(props);

        this.state = {
            title: 'Products Manager',
            products: []
        };
    }

    componentWillMount() {
        this.getProducts()
    }

    getProducts() {
        const products = JSON.parse(localStorage.getItem('products'));
        this.setState({products});
    }

    showProduct(product) {
        return product && <li key={index}>{product.name}, ${product.price}</li>
    }

    showProducts(products) {
        return(
            <ul>
                {products.map((product, index) => this.showProduct(product))}
            </ul>
        )
    }

    render() {
        return (
            <div>
                <h1>{this.state.title}</h1>
                <hr/>
                {this.state.products && this.showProducts()}
            </div>
        );
    }
}

export default Crud;
