import React, {Component} from 'react';

class SandBox extends Component {

    constructor(props) {
        super(props);

        this.onClick = this.onClick.bind(this);
    }

	onClick() {
		console.log(`Price is entered: ${this.input.value}`);
	}

    onChange(event) {
	    document.querySelector("#price").textContent = event.target.value;
    }

	render() {

	    const project = {
	        title: "San Box",
            desc: "A place to practice.",
            price: null
        };

		return (
			<div>
                { project && project.title && <h1>{project.title}</h1> }
                { project && project.desc && <p>{project.desc}</p> }

                <input type="text" onChange={this.onChange} ref={input => this.input = input} />
                <button type="submit" onClick={this.onClick}>Submit</button>
                <p id="price">{ project && project.price && project.price }</p>
			</div>
		)
	}

}

export default SandBox;
